bool button_state = false;

struct Song
{
    const int *melody;
    const int size;
    const int tempo;
    Song(const int *melody, const int size, const int tempo) : melody(melody), size(size), tempo(tempo) {}
    bool play() const
    {
        const int wholenote = 60000 * 4 / tempo;
        for (int thisNote = 0; thisNote < size; thisNote = thisNote + 2)
        {
            const int divider = pgm_read_word_near(melody + thisNote + 1);
            const int noteDuration = divider > 0 ? (wholenote) / divider : (wholenote) / abs(divider) * 1.5;
            tone(BUZZER_PIN, pgm_read_word_near(melody + thisNote), noteDuration * 0.9);
            delay(noteDuration);
            noTone(BUZZER_PIN);
            if (button_state)
            {
                button_state = false;
                delay(100);
                return true;
            }
        }
        return false;
    }
};

